public class CustomValidationMixin extends ValidatorMessageMixin {

 \s\sprivate UserValidationMessagesResolver messageResolver = GWT.create(UserValidationMessagesResolver.class);

 \s\sprivate ValidationMessages messages = GWT.create(ValidationMessages.class);

 \s\s@Override
 \s\spublic String lookup(final String key) {
 \s\s\s\sString value = messageResolver.get(key);
 \s\s\s\sif (value == null) {
 \s\s\s\s\s\stry {
 \s\s\s\s\s\s\s\svalue = key == null ? null : messages.getString(key.replace(".", "_"));
 \s\s\s\s\s\s} catch (MissingResourceException e) {
 \s\s\s\s\s\s\s\svalue = null;
 \s\s\s\s\s\s}
 \s\s\s\s}
 \s\s\s\sreturn value;
 \s\s}

 }
