// Customize the message displayed when the limit is reached
 @UiField MultipleSelect maxOptionsSelect;

 maxOptionsSelect.setMaxOptionsTextHandler(new MaxOptionsTextHandler() {
 \s\s@Override
 \s\spublic String getMaxSelectOptionsText(int maxSelectOptions) {
 \s\s\s\s// Or return "Only {n} options are allowed";
 \s\s\s\sreturn "Only " + maxSelectOptions + " options are allowed";
 \s\s}
 \s\s@Override
 \s\spublic String getMaxGroupOptionsText(int maxGroupOptions) {
 \s\s\s\s// Or return "No more than {n} options in group";
 \s\s\s\sreturn "No more than " + maxGroupOptions + " options in group";
 \s\s}
 });

@UiField Select simpleSelect;\n
@UiField MultipleSelect multiSelect\n\n

// Enable/Disable\n
simpleSelect.setEnabled(true | false);\n
multiSelect.setEnabled(true | false);\n\n

// Show/Hide\n
simpleSelect.setVisible(true | false);\n
multiSelect.setVisible(true | false);\n\n

// Toggle Menu\n
simpleSelect.toggle();\n
multiSelect.toggle();\n\n

// Get Value\n
// If the "value" is not set on an option, the text value is considered as value.\n
String value = simpleSelect.getValue();\n
List<String> values = multiSelect.getValue();\n\n

// Set Value\n
// If the value to be set is not in the item list, no item will be selected.\n
// If you want to fire ValueChangeEvent, add a second argument "true"; it will be fired if\n
// the new value is different than the old one.\n
simpleSelect.setValue("2"[, true]);\n
multiSelect.setValue(Arrays.asList("2", "Relish")[, true]);\n\n

// Render Select\n
simpleSelect.render();\n
multiSelect.render();\n\n

// Refresh Select\n
simpleSelect.refresh();\n
multiSelect.refresh();\n\n

// Select/Deselect All (Multiple Only)\n
multiSelect.selectAll();\n
multiSelect.deselectAll();\n\n
